import React from "react";
import API from "../utils/API";

function BookCard({
  title,
  authors,
  description,
  image,
  link,
  saved = false,
  id,
  onSave,
  onDelete,
  ...rest
}) {
  const handleBookSave = async () => {
    if (saved) {
      await API.deleteBook(id);
      onDelete(id);
    } else {
      const res = await API.saveBook({
        title,
        authors,
        description,
        image,
        link,
      });
      onSave(id, res.id);
    }
  };
  return (
    <div className="card" {...rest}>
      <h1>{title}</h1>
      <p>
        {" "}
        {authors}
        <br />
        <img src={image} />
        <br />
        {description}
        <br />
        <a href={link}>{link}</a>
      </p>

      <div className="media">
        <img
          className="align-self-start mr-3"
          src={image}
          //   picture.large
          alt={title}
        />
        <div className="media-body">
          <h5 className="mt-0">
            <strong>{title}</strong>
          </h5>
          <p>
            Author: {authors}
            <br></br>Description: {description}
            <br></br>Link: <a href={link}>{link}</a>
          </p>
        </div>
      </div>
    </div>
  );
}
export default BookCard;
